import { React, useState } from 'react';
import { v4 as uuid } from 'uuid';
import axios from 'axios';
import styled from 'styled-components';
import Button from '../button/Button';

const TodoForm = ({ setTodos }) => {
  const [titleValue, setTitleValue] = useState('');
  const [contentValue, setContentValue] = useState('');
  const [userIdValue, setUserIdValue] = useState('');
  const [userPw, setUserPw] = useState('');
  const [startDate, setStartDate] = useState('');
  const [schedule, setSchedule] = useState('');


  // 일정제목 입력
  const titleHandler = (e) => {
    setTitleValue(e.target.value);
  };

  // 일정내용 입력
  const contentHandler = (e) => {
    setContentValue(e.target.value);
  };

  // 작성자 입력
  const userIdHandler = (e) => {
    setUserIdValue(e.target.value);
  };

  // 암호 입력
  const todoUserPwHandler = (e) => {
    setUserPw(e.target.value);
  };

  // 시작 날짜
  const startDateHandler = (e) => {
    setStartDate(e.target.value);
  };

  // List 선택
  const scheduleHandler = (e) => {
    setSchedule(e.target.value);
  };

  // 일정 추가하기 이벤트
  const addTodoClick = (event) => {
    // 새로고침 방지
    event.preventDefault();

    // 일정제목,내용, 작성자 빈칸 제거
    const title = titleValue.trim();
    const content = contentValue.trim();
    const userId = userIdValue.trim();

    // 입력값 없을 때 작성X 알림
    if (!title) {
      alert('일정제목을 입력해주세요!');
      setTitleValue('');
      return;
    }
    if (!content) {
      alert('일정내용을 입력해주세요!');
      setContentValue('');
      return;
    }
    if (!userId) {
      alert('담당자를 입력해주세요!');
      setUserIdValue('');
      return;
    }
    if (!userPw) {
      alert('암호를 입력해주세요!');
      setUserPw('');
      return;
    }
    if (!schedule) {
      alert('진행정도를 체크해주세요!');
      setSchedule('');
      return;
    }

    // 일정에 들어갈 key, value
    const newTodo = {
      title: title,
      content: content,
      userId: userId,
      userPw: userPw,
      startDate: startDate,
      schedule: schedule,
      id: uuid(),
    };

    // const A = async () => {
    //   await axios.post("http://localhost:3001/todos", newTodo);
    //   await fetchTodos();
    // };
    // A();

    // 일정 추가 후 인풋창 초기화
    // setTodos((prev) => {
    //   return [...prev, newTodo];
    // });
    setTitleValue('');
    setContentValue('');
    setUserIdValue('');
    setUserPw('');
    setStartDate('');
    setSchedule('');
  };

  return (
    <PopupWrapper>
      <StyleTodoForm>
        <h3 className="form-title">일정추가</h3>
        <form onSubmit={addTodoClick}>
          <StyleInputWrap>
            <div className="checkBox">
              <input
                onChange={scheduleHandler}
                type="radio"
                id="schedule-list0"
                name="schedule-list"
                value={0}
              />
              <label htmlFor="schedule-list0">시작전 </label>
              <input
                onChange={scheduleHandler}
                type="radio"
                id="schedule-list1"
                name="schedule-list"
                value={1}
              />
              <label htmlFor="schedule-list1">시작예정 </label>
              <input
                onChange={scheduleHandler}
                type="radio"
                id="schedule-list2"
                name="schedule-list"
                value={2}
              />
              <label htmlFor="schedule-list2">진행중 </label>
              <input
                onChange={scheduleHandler}
                type="radio"
                id="schedule-list3"
                name="schedule-list"
                value={3}
              />
              <label htmlFor="schedule-list3">완료 </label>
            </div>
          </StyleInputWrap>
          <StyleInputWrap>
            <label htmlFor="new-title">일정제목</label>
            <input
              type="text"
              id="new-title"
              name="new-title"
              onChange={titleHandler}
              value={titleValue}
              autoFocus={true}
              placeholder="일정제목을 적어주세요."
            />
          </StyleInputWrap>
          <StyleInputWrap>
            <label htmlFor="new-content">일정내용 </label>
            <textarea
              cols="300px"
              rows="100px"
              id="new-content"
              name="new-content"
              onChange={contentHandler}
              value={contentValue}
              autoFocus={false}
              placeholder="일정내용을 적어주세요."
            />
          </StyleInputWrap>
          <StyleInputWrap>
            <label htmlFor="start-date">마감일자</label>
            <input
              onChange={startDateHandler}
              type="date"
              id="start-date"
              name="start-date"
              min="2022-01-01"
              max="2023-03-15"
            />
          </StyleInputWrap>

          <StyleFlex>
            <StyleInputWrap>
              <label htmlFor="new-userid">담당자</label>
              <input
                type="text"
                id="new-userid"
                name="new-userid"
                onChange={userIdHandler}
                value={userIdValue}
                autoFocus={false}
                placeholder="작성자를 적어주세요."
              />
            </StyleInputWrap>
            <StyleInputWrap>
              <label htmlFor="new-userpw">비밀번호</label>
              <input
                type="password"
                id="new-userpw"
                name="new-userpw"
                onChange={todoUserPwHandler}
                value={userPw}
                autoFocus={false}
                placeholder="암호를 적어주세요."
              />
            </StyleInputWrap>
          </StyleFlex>

          <StyleButton>
            <Button radius="4">일정 추가</Button>
            <Button
              radius="4"
              backgroundColor="#878787"
              sidePadding="28"
              upDownPadding="10"
            >
              취소
            </Button>
          </StyleButton>
        </form>
      </StyleTodoForm>
    </PopupWrapper>
  );
};
export default TodoForm;
const StyleFlex = styled.div`
  display: flex;
  align-items: center;
  gap: 16px;
`;
const PopupWrapper = styled.div`
  position: fixed;
  top: 0;
  display: flex;
  justify-content: center;
  align-items: center;
  width: 100%;
  height: 100%;
  padding: 3em 0;
  background-color: rgba(0, 0, 0, 0.5);
`;
const StyleTodoForm = styled.div`
  width: 500px;
  padding: 24px;
  background-color: #fff;

  .form-title {
    font-size: 24px;
    font-weight: 600;
    margin-bottom: 16px;
  }
`;

const StyleInputWrap = styled.div`
  width: 100%;
  margin-bottom: 16px;

  > label {
    display: block;
    font-weight: 600;
    margin-bottom: 8px;
  }

  > input,
  textarea {
    width: 100%;
    box-sizing: border-box;
  }

  > input {
    height: 30px;

    [type='radio'] {
      cursor: pointer;
    }
  }
  > textarea {
    height: 120px;
    resize: none;
  }
`;
const StyleButton = styled.div`
  display: flex;
  justify-content: flex-end;
  gap: 8px;
`;
