import React, { useState } from 'react';
import styled from 'styled-components';
import Button from '../button/Button';
import { v4 as uuidv4 } from 'uuid';

function CmWrite({ setCommentList, commentList }) {
  const [commentWrite, setCommentWrite] = useState('');

  //댓글 입력창
  const commentOnChange = (e) => {
    setCommentWrite(e.target.value);
  };

  //댓글 추가버튼
  const commentOnSubmit = (e) => {
    e.preventDefault();
    let now = new Date().getFullYear().getDate();
    console.log(now);

    const newComment = {
      id: uuidv4(),
      comment: commentWrite,
      userName: 'user1',
      date: now,
    };

    setCommentList([...commentList, newComment]);
  };

  return (
    <>
      <StyleForm onSubmit={commentOnSubmit}>
        <input
          maxLength="40"
          type="text"
          value={commentWrite}
          onChange={commentOnChange}
          placeholder="댓글을 입력해주세요."
        />
        <Button backgroundColor="#0a0a0a" radius="100">
          댓글추가
        </Button>
      </StyleForm>
    </>
  );
}

export default CmWrite;

const StyleForm = styled.form`
  display: flex;
  justify-content: space-between;
  padding: 16px 24px;
  border-top: 1px solid #ddd;
  border-bottom: 1px solid #ddd;

  > input {
    width: 60%;
    border: none;
    :focus {
      outline: none;
    }
  }
`;
