import React, { useEffect, useState } from 'react';
import styled from 'styled-components';
import { useLocation, useNavigate } from 'react-router-dom';
import { useSelector, useDispatch } from 'react-redux';
import { __getTodos, __modifyEdittedTodo } from '../../modules/todosSlice';
import DetailScheduleEdit from '../DetailPage/DetailScheduleEdit';
import Comment from '../comments/Comment';
import Button from '../button/Button';

function DetailMain() {
  const dispatch = useDispatch();

  const navigation = useNavigate();
  // useNavigate로 전달한 props(todo의 id)
  const location = useLocation();
  const todoId = location.pathname.split('/')[1];

  // 상세페이지 제목을 받아오려면 todos를 일단 가져오자.
  const { todos } = useSelector((state) => state.allTodos);

  //. todoId와 같은 id를 가진 todo를 filter를 통해 변수에 할당하자.
  const sameIdTodos = todos.filter((item) => item.id === todoId)[0];

  const todosSchedule = sameIdTodos && sameIdTodos.schedule;
  const todosStartDate = sameIdTodos && sameIdTodos.startDate;
  const todosDoneDate = sameIdTodos && sameIdTodos.doneDate;
  const todosTitle = sameIdTodos && sameIdTodos.title;
  const todosContent = sameIdTodos && sameIdTodos.content;
  const todosUserId = sameIdTodos && sameIdTodos.userId;

  // 진행 상태 수정 스위치
  const [buttonSwitch, setButtonSwitch] = useState(false);
  const openScheduleEditButton = () => {
    setButtonSwitch(!buttonSwitch);
  };

  const [editValue, setEditValue] = useState({
    editContent: todosContent,
    editTitle: todosTitle,
    editDoneDate: todosDoneDate,
    editUserId: todosUserId,
    editSchedule: todosSchedule,
  });

  const { editContent, editTitle, editDoneDate, editUserId, editSchedule } =
    editValue;

  const onSubmitEdittedTodo = (e) => {
    e.preventDefault();

    const passwordForModifying = window.prompt(
      '수정을 위해 비밀 번호를 입력해 주세요.'
    );

    if (passwordForModifying === sameIdTodos.userPw) {
      dispatch(
        __modifyEdittedTodo({
          id: todoId,
          userId: editUserId,
          title: editTitle,
          content: editContent,
          doneDate: editDoneDate,
          schedule: editSchedule,
        })
      );

      setEditValue({
        ...editValue,
        editContent: editContent,
        editTitle: editTitle,
        editDoneDate: editDoneDate,
        editUserId: editUserId,
        editSchedule: editSchedule,
      });

      window.confirm('수정이 완료되었습니다.');
    } else {
      alert('비밀번호가 틀렸습니다.');
      setEditValue({
        ...editValue,
        editContent: todosContent,
        editTitle: todosTitle,
        editDoneDate: todosDoneDate,
        editUserId: todosUserId,
        editSchedule: todosSchedule,
      });
    }
  };

  useEffect(() => {
    dispatch(__getTodos());
  }, [dispatch]);

  return (
    <>
      {buttonSwitch && (
        <StyledDetailMain onClick={openScheduleEditButton}>
          <form onSubmit={onSubmitEdittedTodo}>
            <ButtonWrapper>
              <Button
                backgroundColor="#484848"
                radius="4"
                ClickHandler={() => {
                  navigation('/');
                }}
              >
                뒤로가기
              </Button>
              <Button radius="4">수정하기</Button>
            </ButtonWrapper>

            {/* 제목 */}
            <StyleContent className="title">
              <input
                placeholder={todosTitle}
                value={editTitle}
                onChange={(e) => {
                  const { value } = e.target;
                  setEditValue({
                    ...editValue,
                    editTitle: value,
                  });
                }}
              />
            </StyleContent>
            {/* 담당자 */}
            <StyleContent>
              <ConentTitle>담당자 </ConentTitle>
              <input
                placeholder={todosUserId}
                value={editUserId}
                onChange={(e) => {
                  const { value } = e.target;
                  setEditValue({
                    ...editValue,
                    editUserId: value,
                  });
                }}
              />
            </StyleContent>

            {/* 진행상태 */}
            <StyleContent>
              <ConentTitle>진행 상태 </ConentTitle>

              <div>
                <p>{editSchedule ? editSchedule : todosSchedule}</p>

                <div className="check-box">
                  <label htmlFor="schedule-list0">
                    <input
                      onChange={(e) => {
                        const { value } = e.target;
                        setEditValue({
                          ...editValue,
                          editSchedule: value,
                        });
                      }}
                      type="radio"
                      id="schedule-list0"
                      name="schedule-list"
                      value={'시작전'}
                      defaultChecked
                    />
                    시작전
                  </label>
                  <label htmlFor="schedule-list1">
                    <input
                      onChange={(e) => {
                        const { value } = e.target;
                        setEditValue({
                          ...editValue,
                          editSchedule: value,
                        });
                      }}
                      type="radio"
                      id="schedule-list1"
                      name="schedule-list"
                      value={'시작예정'}
                    />
                    시작예정
                  </label>

                  <label htmlFor="schedule-list2">
                    <input
                      onChange={(e) => {
                        const { value } = e.target;
                        setEditValue({
                          ...editValue,
                          editSchedule: value,
                        });
                      }}
                      type="radio"
                      id="schedule-list2"
                      name="schedule-list"
                      value={'진행중'}
                    />
                    진행중
                  </label>

                  <label htmlFor="schedule-list3">
                    <input
                      onChange={(e) => {
                        const { value } = e.target;
                        setEditValue({
                          ...editValue,
                          editSchedule: value,
                        });
                      }}
                      type="radio"
                      id="schedule-list3"
                      name="schedule-list"
                      value={'완료'}
                    />
                    완료
                  </label>
                </div>
              </div>
            </StyleContent>

            {/* 마감일시 */}
            <StyleContent>
              <ConentTitle>마감 일시 </ConentTitle>
              <input
                placeholder={todosDoneDate}
                value={editDoneDate}
                onChange={(e) => {
                  const { value } = e.target;
                  setEditValue({
                    ...editValue,
                    editDoneDate: value,
                  });
                }}
                type="date"
              />
            </StyleContent>
            {/* 내용 */}
            <StyleText>
              <ConentTitle>내용</ConentTitle>
              <textarea
                placeholder={todosContent}
                value={editContent}
                onChange={(e) => {
                  const { value } = e.target;
                  setEditValue({
                    ...editValue,
                    editContent: value,
                  });
                }}
              />
            </StyleText>
          </form>
          <Comment />
        </StyledDetailMain>
      )}
    </>
  );
}

export default DetailMain;

//CSS

const ButtonWrapper = styled.div`
  display: flex;
  justify-content: space-between;
  margin-bottom: 2em;
`;

const StyledDetailMain = styled.div`
  width: 1200px;
  margin: 0 auto;

  @media (max-width: 1200px) {
    width: 100%;
    padding: 24px;
    box-sizing: border-box;
  }

  .title {
    margin-bottom: 1em;
    > input {
      border: none;
      font-size: 24px;
      color: #000;
      outline: none;

      ::placeholder {
        font-size: 24px;
        color: #000;
      }
    }
  }
`;

const StyleContent = styled.div`
  display: flex;
  width: 80%;
  margin-bottom: 0.5em;

  .title {
    display: flex;
  }

  > input {
    display: block;
    flex: 1;
    border: none;
    border-bottom: 1px solid #ddd;
    outline: none;
  }
`;

const ConentTitle = styled.div`
  font-weight: 900;
  flex: 1;
`;

const StyleText = styled.div`
  margin: 1.5em 0;
  border-top: 1px solid #ddd;
  padding: 2em 0;

  > textarea {
    width: 100%;
    border: none;
    margin-top: 1em;
    min-height: 200px;
    border-radius: 4px;
    resize: none;
    outline: none;
    font-size: 16px;
    color: #000;

    ::placeholder {
      font-size: 16px;
      color: #000;
    }
  }
`;
