import { createAsyncThunk, createSlice } from '@reduxjs/toolkit';
import axios from 'axios';

//initialState
const initialState = {
  commentLists: [
    {
      comment: '3',
      userId: '2',
      userPw: '2',
      date: '2022. 12. 24.',
      id: 1,
    },
  ],
  isLoading: false,
  error: null,
};

//Thunk 함수
export const __getComment = createAsyncThunk(
  'getComment',
  async (payload, thunkAPI) => {
    try {
      const data = await axios.get('http://localhost:3001/commentLists');
      console.log('data', data);
      return thunkAPI.fulfillWithValue(data.data);
    } catch (error) {
      console.log(error);
      return thunkAPI.rejectWithValue(error);
    }
  }
);

export const __addComment = createAsyncThunk(
  'addComment',
  (payload, thunkAPI) => {
    try {
      const commentList = axios.post(
        'http://localhost:3001/commentLists',
        commentList
      );
      return thunkAPI.fulfillWithValue(commentList);
    } catch (error) {
      console.log(error);
      return thunkAPI.rejectWithValue(error);
    }
  }
);

const commentSlice = createSlice({
  name: 'commentList',
  initialState,
  reducers: {
    addComment: (state, action) => {
      return [...state, action.payload];
    },
    deleteComment: (state, action) => {
      return state.filter((c) => c.id !== action.payload);
    },
    modifyComment: (state, action) => {
      console.log('action', action.payload);

      state.forEach((c) => {
        console.log('state', state);
        if (c.id === action.payload.id) {
          c.comment = action.payload.comment;
          return;
          //   return { ...c, comment: action.payload.comment };
        }
      });
      return state;
    },
  },
  extraReducers: {
    [__getComment.pending]: (state) => {
      state.isLoading = true;
    },
    [__getComment.fulfilled]: (state, action) => {
      state.isLoading = false;
      state.commentLists = action.payload;
      console.log('fulfilled 상태', state, action);
    },
    [__getComment.rejected]: (state, action) => {
      state.isLoading = false;
      state.error = action.payload;
    },
    [__addComment.pending]: (state) => {
      state.isLoading = true;
    },
    [__addComment.fulfilled]: (state, action) => {
      state.isLoading = false;
      state.commentLists = action.payload;
      console.log('fulfilled 상태', state, action);
    },
    [__addComment.rejected]: (state, action) => {
      state.isLoading = false;
      state.error = action.payload;
    },
  },
});

export const { getComment } = commentSlice.actions;
export default commentSlice.reducer;
