import React, { useState } from 'react';
import styled from 'styled-components';
import Cmview from './Cmview';
import CmWrite from './CmWrite';

function Comment() {
  const [commentLists, setCommentLists] = useState([]);
  const fetchComment = async () => {
    const { data } = await axios.get('http://localhost:3001/commentLists');
    setCommentLists(data);
  };
  useEffect(() => {
    fetchComment();
  }, []);

  return (
    <StyleCommentWrap>
      <CmWrite commentLists={commentLists} setCommentLists={setCommentLists} />
      <Cmview commentLists={commentLists} setCommentLists={setCommentLists} />
    </StyleCommentWrap>
  );
}

export default Comment;

const StyleCommentWrap = styled.div`
  width: calc(100% - 24px);
  margin: 16px auto;
  border-top: 1px solid #e6e6e6;
  border-bottom: 1px solid #e6e6e6;
`;
