import React, { useState } from 'react';
import styled from 'styled-components';
import Button from '../button/Button';
import { v4 as uuidv4 } from 'uuid';

function CmWrite({ setCommentList, commentList }) {
  const [comment, setComment] = useState('');
  const [userId, setUserId] = useState('');
  const [userPw, setUserPw] = useState('');

  //댓글 입력창
  const commentOnChange = (e) => {
    setComment(e.target.value);
  };
  //유저이름 입력창
  const userIdOnChange = (e) => {
    setUserId(e.target.value);
  };
  //비밀번호 입력창
  const userPwOnChange = (e) => {
    setUserPw(e.target.value);
  };

  //댓글 추가버튼
  const commentOnSubmit = (e) => {
    e.preventDefault();
    //현재시간 객체로 받음.
    let today = new Date();

    const newComment = {
      id: uuidv4(),
      comment,
      userId,
      userPw,
      date: today.toLocaleDateString(),
    };

    if(!comment)

    setCommentList([...commentList, newComment]);
  };

  return (
    <>
      <StyleForm onSubmit={commentOnSubmit}>
        <input
          maxLength="40"
          type="text"
          value={comment}
          onChange={commentOnChange}
          placeholder="댓글을 입력해주세요."
        />

        <StyleFlex>
          <div className="user-infor">
            <input
              maxLength="5"
              type="text"
              value={userId}
              onChange={userIdOnChange}
              placeholder="이름을 입력해주세요"
            />
            <input
              maxLength="10"
              type="text"
              value={userPw}
              onChange={userPwOnChange}
              placeholder="비밀번호를 입력 해 주세요"
            />
          </div>
          <Button backgroundColor="#0a0a0a" radius="100">
            댓글추가
          </Button>
        </StyleFlex>
      </StyleForm>
    </>
  );
}

export default CmWrite;

const StyleForm = styled.form`
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 16px 24px;

  > input {
    flex: 1;
    height: 34px;
    border: none;
    border-radius: 50px;
    padding: 0 8px;

    :focus {
      outline: none;
    }
  }
`;

const StyleFlex = styled.div`
  display: flex;
  flex: 1.5;
  justify-content: flex-end;

  .user-infor {
    > input {
      height: 34px;
      padding: 0 8px;
      margin-right: 8px;
      border: none;
      border-radius: 50px;

      :focus {
        outline: none;
      }
    }
  }
`;
