import React, { useEffect, useState } from 'react';
import { useSelector } from 'react-redux';
import CmList from './CmList';
import axios from 'axios';

function Cmview({ setCommentLists }) {
  const commentList = useSelector((state) => state.commentList);

  const fetchComment = async () => {
    const { data } = await axios.get('http://localhost:3001/commentLists');
    setCommentLists(data);
  };

  useEffect(() => {
    fetchComment();
  }, []);

  return (
    <div>
      {commentLists.map((item) => {
        return <CmList item={item} key={item.id} />;
      })}
    </div>
  );
}

export default Cmview;
