import React, { useEffect, useState } from 'react';
import styled from 'styled-components';
import Button from '../button/Button';
import { useDispatch } from 'react-redux';
import { __addComment } from '../../modules/commentSlice';
import { nanoid } from '@reduxjs/toolkit';

function CommentWrite({ commentId }) {
  const dispatch = useDispatch();

  let today = new Date();
  const [commentList, setCommentList] = useState({
    id: commentId,
    // order: 1,
    comment: '',
    userId: '',
    userPw: '',
    date: today.toLocaleDateString(),
  });

  const { comment, userId, userPw } = commentList;

  const [inputTxt, setInpuTxt] = useState({
    commentTxt: '',
    userIdTxt: '',
    userPwTxt: '',
  });
  const { commentTxt, userIdTxt, userPwTxt } = inputTxt;

  //추가
  const commentOnsubmitHandler = () => {
    if (!comment || !userId || !userPw) {
      setInpuTxt({
        ...inputTxt,
        commentTxt: '댓글 을 입력해주세요.',
        userIdTxt: '이름 을 입력해주세요.',
        userPwTxt: '비밀번호 를 입력해주세요.',
      });
      return;
    }

    const newComment = {
      id: nanoid(),
      postId: commentId,
      comment,
      userId,
      userPw,
      date: today.toLocaleDateString(),
    };

    dispatch(__addComment(newComment));
    setCommentList({ ...commentList, comment: '', userId: '', userPw: '' });
  };

  return (
    <>
      <StyleForm
        onSubmit={(e) => {
          e.preventDefault();
          commentOnsubmitHandler();
        }}
      >
        <div className="comment-wrap">
          <input
            maxLength="40"
            type="text"
            value={comment}
            onChange={(e) => {
              const { value } = e.target;
              setCommentList({
                ...commentList,
                comment: value,
              });
            }}
            placeholder="댓글을 입력해주세요."
          />
          {/* 코멘트 입력했을때  */}
          <StyleInputErro>{comment ? '' : commentTxt}</StyleInputErro>
        </div>

        <StyleFlex>
          <div className="user-infor">
            <div>
              <input
                maxLength="5"
                type="text"
                value={userId}
                onChange={(e) => {
                  const { value } = e.target;
                  setCommentList({
                    ...commentList,
                    userId: value,
                  });
                }}
                placeholder="이름을 입력해주세요"
              />
              <StyleInputErro>{userId ? '' : userIdTxt}</StyleInputErro>
            </div>

            <div>
              <input
                maxLength="10"
                type="text"
                value={userPw}
                onChange={(e) => {
                  const { value } = e.target;
                  setCommentList({
                    ...commentList,
                    userPw: value,
                  });
                }}
                placeholder="비밀번호를 입력 해 주세요"
              />
              <StyleInputErro>{userPw ? '' : userPwTxt}</StyleInputErro>
            </div>
          </div>
        </StyleFlex>
        <Button backgroundColor="#0a0a0a" radius="100">
          댓글추가
        </Button>
      </StyleForm>
    </>
  );
}

export default CommentWrite;

const StyleForm = styled.form`
  display: flex;
  justify-content: space-between;
  align-items: flex-start;
  padding: 16px 24px;

  .comment-wrap {
    flex: 1;

    > input {
      width: 100%;
      height: 34px;
      border: none;
      padding: 0 8px;
      border-bottom: 1px solid #e6e6e6;

      :focus {
        outline: none;
      }
    }
  }
`;

const StyleFlex = styled.div`
  display: flex;
  flex: 1.5;
  justify-content: flex-end;

  .user-infor {
    display: flex;

    > div {
      > input {
        height: 34px;
        padding: 0 8px;
        margin-right: 8px;
        border: none;
        border-bottom: 1px solid #e6e6e6;

        :focus {
          outline: none;
        }
      }
    }
  }
`;

const StyleInputErro = styled.p`
  color: red;
  font-size: 13px;
  margin-top: 4px;
`;
